import client from "axios";

const api = "https://jsonplaceholder.typicode.com"; // Não observei as requisições no network!

export const getPostsList = () => client.get(api + "/posts");

export const createPost = (data) => client.post(api + "/posts" +
  {  
  'id': 0,
  'title': data.title,
  'body': data.body,
  'userId': data.userId
  }
);

export const getPost = (id) => client.get(api + "/posts/" + id);

export const putPost = (data) => client.put(api + "/posts/" + data.id, data.body);

export const deletePost = (id) => client.delete(api + "/posts/" + id);
